CREATE TABLE `chat`.`status` (`id` INT(11) NOT NULL AUTO_INCREMENT , `unique_id` INT(50) NULL DEFAULT NULL , `status` VARCHAR(255) NULL DEFAULT NULL , `statusmessage` VARCHAR(255) NULL DEFAULT NULL , `color` VARCHAR(50) NULL DEFAULT NULL , `time` TIMESTAMP(6) NOT NULL DEFAULT CURRENT_TIMESTAMP(6) , PRIMARY KEY (`id`)) ENGINE = InnoDB