CREATE TABLE `hackathon`.`products` (`id` INT(11) NOT NULL AUTO_INCREMENT , `unique_id` INT(6) NULL DEFAULT NULL , `name` VARCHAR(255) NULL DEFAULT NULL , `price` INT(9) NULL DEFAULT NULL , `quantity` INT(100) NULL DEFAULT NULL , `image` VARCHAR(255) NULL DEFAULT NULL , `description` VARCHAR(255) NULL DEFAULT NULL , `time` TIMESTAMP(6) NOT NULL DEFAULT CURRENT_TIMESTAMP(6) , PRIMARY KEY (`id`)) ENGINE = InnoDB;